/*	--------------------------------------------------
	:: To Top
	-------------------------------------------------- */

jQuery(document).ready(function($){
	var offset = 300,
		offset_opacity = 1200,
		scroll_top_duration = 1000,
		$back_to_top = $('.to-top');

	$back_to_top.on('click', function(event){
		event.preventDefault();
		$('body,html').animate({
			scrollTop: 0 ,
		 	}, scroll_top_duration
		);
	});
});